export class TagBadge {
    constructor() {
        /** The accent colour */
        this.accent = 'dolphinblue';
        /** The size of the badge */
        this.size = 'large';
    }
    render() {
        const className = `tag-badge--${this.accent} tag-badge-size--${this.size}`;
        return h("span", { class: className }, this.value);
    }
    static get is() { return "tag-badge"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "accent": {
            "type": String,
            "attr": "accent"
        },
        "size": {
            "type": String,
            "attr": "size"
        },
        "value": {
            "type": String,
            "attr": "value"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-badge:**/"; }
}
