export class TagBar {
    constructor() {
        /** The % value of the bar */
        this.value = 50;
        /** The width of the container */
        this.width = '100px';
        /** The height of the container */
        this.height = '8px';
        /** The accent colour */
        this.accent = 'dolphinblue';
    }
    render() {
        const barClassName = `tag-bar--${this.accent}`;
        const textWidth = this.inline ? undefined : this.width;
        const flexDirection = this.inline ? 'row' : 'column';
        const alignItems = this.inline ? 'center' : undefined;
        const textLeftMargin = this.inline ? '5px' : undefined;
        const textContainer = this.inline
            ? 'tag-bar__text-container tag-bar__text-container--inline'
            : 'tag-bar__text-container';
        return (h("div", { class: "tag-bar", style: { flexDirection, alignItems } },
            h("div", { class: "tag-bar__bar-container", style: { width: this.width, height: this.height } },
                h("div", { class: barClassName, style: { width: `${this.value}%`, height: this.height } })),
            this.text && (h("div", { class: textContainer, style: { width: textWidth, marginLeft: textLeftMargin } },
                h("tag-text", { type: "span" }, this.text)))));
    }
    static get is() { return "tag-bar"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "accent": {
            "type": String,
            "attr": "accent"
        },
        "height": {
            "type": String,
            "attr": "height"
        },
        "inline": {
            "type": Boolean,
            "attr": "inline"
        },
        "text": {
            "type": String,
            "attr": "text"
        },
        "value": {
            "type": Number,
            "attr": "value"
        },
        "width": {
            "type": String,
            "attr": "width"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-bar:**/"; }
}
