/**
 * This has been deprecated
 * tag-kpi should be used instead
 */
export class TagCardKpi {
    constructor() {
        /** The background colour for the KPI */
        this.accent = 'dolphinblue';
        /** Whether the value is under the label (vertical) or at the side (horizontal) */
        this.orientation = 'vertical';
    }
    render() {
        const { accent, label, value, orientation } = this;
        return h("tag-stat", { accent: accent, label: label, value: value, orientation: orientation });
    }
    static get is() { return "tag-card-kpi"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "accent": {
            "type": String,
            "attr": "accent"
        },
        "label": {
            "type": String,
            "attr": "label"
        },
        "orientation": {
            "type": String,
            "attr": "orientation"
        },
        "value": {
            "type": String,
            "attr": "value"
        }
    }; }
}
