export class TagCardOkr {
    constructor() {
        /** Highlight the left border with an access colour */
        this.accent = 'info';
        /** The heading type */
        this.headingType = 'h3';
    }
    render() {
        const { accent, date, heading, headingType, person, progressValue, progressSize } = this;
        return (h("tag-card", { accent: accent },
            h("div", { style: { display: 'flex', flexDirection: 'column', alignItems: 'center' } },
                h("div", null,
                    h("tag-progress-donut", { value: progressValue, width: progressSize, accent: accent })),
                h("div", null,
                    heading && (h("tag-text", { type: headingType, "class-name": "text-center" }, heading)),
                    h("div", { style: { display: 'flex', alignItems: 'center' } },
                        person && (h("div", { style: { marginRight: '10px' } },
                            h("tag-icon", { icon: "User", accent: "title", style: { marginRight: '3px' } }),
                            h("tag-text", { type: "span", text: person }))),
                        date && (h("div", null,
                            h("tag-icon", { icon: "Calendar", accent: "title", style: { marginRight: '3px' } }),
                            h("tag-text", { type: "span", text: date }))))))));
    }
    static get is() { return "tag-card-okr"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "accent": {
            "type": String,
            "attr": "accent"
        },
        "date": {
            "type": String,
            "attr": "date"
        },
        "heading": {
            "type": String,
            "attr": "heading"
        },
        "headingType": {
            "type": String,
            "attr": "heading-type"
        },
        "person": {
            "type": String,
            "attr": "person"
        },
        "progressSize": {
            "type": String,
            "attr": "progress-size"
        },
        "progressValue": {
            "type": Number,
            "attr": "progress-value"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-card-okr:**/"; }
}
