/* Hold options for tag-edit-field */
export class TagEditFieldOption {
    /** sets the value prop to the text content if not set */
    componentWillLoad() {
        if (this.value === undefined) {
            this.value = this.element.textContent;
        }
    }
    render() {
        return null;
    }
    static get is() { return "tag-edit-field-option"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "element": {
            "elementRef": true
        },
        "selected": {
            "type": Boolean,
            "attr": "selected"
        },
        "value": {
            "type": String,
            "attr": "value",
            "mutable": true
        }
    }; }
    static get style() { return "/**style-placeholder:tag-edit-field-option:**/"; }
}
