export class TagFormGroup {
    render() {
        return (h("div", { class: this.getClassNames() },
            h("slot", null)));
    }
    getClassNames() {
        let classNames = 'tag-form-group';
        if (this.inline) {
            classNames = classNames + ' tag-form-group-inline';
        }
        if (this.borderless) {
            classNames = classNames + ' tag-form-group-borderless';
        }
        return classNames;
    }
    static get is() { return "tag-form-group"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "borderless": {
            "type": Boolean,
            "attr": "borderless"
        },
        "inline": {
            "type": Boolean,
            "attr": "inline"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-form-group:**/"; }
}
