export class TagToast {
    constructor() {
        /** The position of the toast */
        this.position = 'bottom-right';
    }
    render() {
        return (h("div", { class: `tag-toast tag-toast__container tag-toast--${this.position}`, "aria-live": "polite" },
            h("slot", null)));
    }
    static get is() { return "tag-toast"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "position": {
            "type": String,
            "attr": "position"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-toast:**/"; }
}
