/* Holds validation rules for a tag-field */
export class TagValidation {
    constructor() {
        /** When the validation rule should be executed */
        this.executeOn = 'blur';
    }
    render() {
        return null;
    }
    static get is() { return "tag-validation"; }
    static get encapsulation() { return "shadow"; }
    static get properties() { return {
        "args": {
            "type": "Any",
            "attr": "args"
        },
        "executeOn": {
            "type": String,
            "attr": "execute-on"
        },
        "message": {
            "type": String,
            "attr": "message"
        },
        "rule": {
            "type": String,
            "attr": "rule"
        }
    }; }
    static get style() { return "/**style-placeholder:tag-validation:**/"; }
}
